import "./style.css";
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Board from "../../components/Board/Board";
import PlayerInfo from "../../components/Playerinfo/PlayerInfo";
import History from "../../components/History/History";
import { connect } from "react-redux";
import PropTypes from "prop-types";

class Game extends Component {
  static PropTypes = {
    squares: PropTypes.string.isRequired,
    user: PropTypes.object,
  };
  componentWillMount() {}
  getCurrentPlayer() {
    //根据步数，判断奇偶，确定棋手
    return this.state.stepCount % 2 === 0 ? "X" : "O";
  }

  handleClickSquare = (position, info) => {
    if (info === null) {
      const { history } = this.state;
      const newHistory = [
        ...history,
        {
          stepCount: this.state.stepCount,
          player: this.getCurrentPlayer(),
          position: position,
        },
      ];

      this.setState({
        history: newHistory,
        stepCount: this.state.stepCount + 1,
        squares: this.calcSquare(newHistory, this.state.stepCount + 1),
      });
    }
  };

  calcSquare(history, stepCount) {
    const newSquare = new Array(9).fill(null);
    for (let i = 0; i < stepCount; i++) {
      let curHistory = history[i];
      newSquare[curHistory.position] = curHistory.player;
    }
    return newSquare;
  }

  handleClickHistory(item) {
    console.log(item);
  }

  render() {
    const curPlayer = this.getCurrentPlayer();
    const { squares, history } = this.state;
    return (
      <>
        <div className="App">
          <div className="leftPanel">
            <Board squares={squares} def={this.handleClickSquare} />
          </div>
          <div className="rightPanel">
            <PlayerInfo curPlayer={curPlayer} />
            <History
              history={history}
              onClickHistory={this.handleClickHistory}
            />
          </div>
        </div>
        <div className="button">
          <button>
            <Link to="/">Exit</Link>
          </button>
          <button>
            <Link to="/again">Play again</Link>
          </button>
        </div>
      </>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    count: state.count,
  };
};

export default Game = connect(mapStateToProps)(Game);
