import {
  UPDATE_STEPCOUNT,
  UPDATE_HISTORY,
  UPDATE_SQUARE,
} from "../actions/actionGame";
//reducer
export const reduceGame = (previousState = initData, action) => {
  let nextState = JSON.parse(JSON.stringify(previousState));
  switch (action.type) {
    case UPDATE_STEPCOUNT:
      nextState.stepCount++;
      return nextState;
    case UPDATE_HISTORY:
      return nextState;
    case UPDATE_SQUARE:
      return nextState;
    default:
      return previousState;
  }
};
const initData = {
  //棋盘上的棋子信息
  squares: new Array(9).fill(null),
  //步数
  stepCount: 0,
  //历史记录
  history: [],
};
