//生产者
export const reducerLogin = (previousState = initData, action) => {
  let nextState = JSON.parse(JSON.stringify(previousState));
};
const initData = {
  //棋盘上的棋子信息
  squares: new Array(9).fill(null),
  //步数
  stepCount: 0,
  //历史记录
  history: [],
  //胜负已出
  over: {
    result: false,
    winner: "",
  },
};
