export const winner = (currentHistory) => {
  let X = [];
  let O = [];
  for (const element of currentHistory) {
    switch (element.player) {
      case "X":
        X.push(element.position);
        break;
      case "O":
        O.push(element.position);
        break;
      default:
    }
  }
  if (
    X.includes(0, 1, 2) ||
    X.includes(3, 4, 5) ||
    X.includes(6, 7, 8) ||
    X.includes(0, 3, 6) ||
    X.includes(1, 4, 7) ||
    X.includes(2, 5, 8) ||
    X.includes(0, 4, 8) ||
    X.includes(2, 4, 6)
  ) {
    return {
      result: true,
      winner: "X",
    };
  }
};
