export const winner = (currentHistory) => {
  let X = [];
  let O = [];
  for (const element of currentHistory) {
    switch (element.player) {
      case "X":
        X.push(element.position);
        console.log("X content" + X);
        break;
      case "O":
        O.push(element.position);
        console.log("O content" + O);
        break;
      default:
    }
  }
  if (
    X.includes(0, 1, 2) ||
    X.includes(3, 4, 5) ||
    X.includes(6, 7, 8) ||
    X.includes(0, 3, 6) ||
    X.includes(1, 4, 7) ||
    X.includes(2, 5, 8) ||
    X.includes(0, 4, 8) ||
    X.includes(2, 4, 6)
  ) {
    return {
      result: true,
      winner: "X",
    };
  }
  if (
    O.includes(0, 1, 2) ||
    O.includes(3, 4, 5) ||
    O.includes(6, 7, 8) ||
    O.includes(0, 3, 6) ||
    O.includes(1, 4, 7) ||
    O.includes(2, 5, 8) ||
    O.includes(0, 4, 8) ||
    O.includes(2, 4, 6)
  ) {
    return {
      result: true,
      winner: "O",
    };
  }
  return {
    result: false,
    winner: "",
  };
};
