import "./style.css";
import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
import Board from "../../components/Board/Board";
import PlayerInfo from "../../components/Playerinfo/PlayerInfo";
import History from "../../components/History/History";

class Game extends Component {
  getCurrentPlayer() {
    //根据步数，判断奇偶，确定棋手
    return this.props.stepCount % 2 === 0 ? "X" : "O";
  }

  handleClickSquare = (position, info) => {
    if (info === null) {
      const newHistory = [
        ...this.props.history,
        {
          stepCount: this.props.stepCount,
          player: this.getCurrentPlayer(),
          position: position,
        },
      ];

      this.props.updateGame();
      ({
        history: newHistory,
        stepCount: this.props.stepCount + 1,
        squares: this.calcSquare(newHistory, this.props.stepCount + 1),
      });
    }
  };

  render() {
    const curPlayer = this.getCurrentPlayer();
    return (
      <Fragment>
        <div className="App">
          <div className="leftPanel">
            <Board
              squares={this.props.squares}
              def={this.props.handleClickSquare}
            />
          </div>
          <div className="rightPanel">
            <PlayerInfo curPlayer={curPlayer} />
            <History history={this.props.history} />
          </div>
        </div>
        <div className="button">
          <button>
            <Link to="/">Exit</Link>
          </button>
          <button>
            <Link to="/again">Play again</Link>
          </button>
        </div>
      </Fragment>
    );
  }
}
export default Game;
