import { UPDATE_GAME } from "../actions/actionGame";
//生产者
export const reducerGame = (previousState = initData, action) => {
  let nextState = JSON.parse(JSON.stringify(previousState));
  switch (action.type) {
    case UPDATE_GAME:
      nextState.stepCount++;
      return nextState;
    default:
      return previousState;
  }
};
const initData = {
  //棋盘上的棋子信息
  squares: new Array(9).fill(null),
  //步数
  stepCount: 0,
  //历史记录
  history: [],
};
const calcSquare = (history, stepCount) => {
  const newSquare = new Array(9).fill(null);
  for (let i = 0; i < stepCount; i++) {
    let curHistory = history[i];
    newSquare[curHistory.position] = curHistory.player;
  }
  return newSquare;
};
