//reducer
export const reduceGame = (state = initData, action) => {
  let nextState = JSON.parse(JSON.stringify(state));
  switch (action.type) {
    case UPDATE_STEPCOUNT:
      return nextState;
    default:
      return state;
  }
};
const initData = {
  //棋盘上的棋子信息
  squares: new Array(9).fill(null),
  //步数
  stepCount: 0,
  //历史记录
  history: [],
};
