export const UPDATE_GAME = "UPDATE_GAME";

export const RESET_DATA = "RESET_DATA";

export const USER_NAME = "USER_NAME";

export const getAction = (actionName = RESET_DATA, ...param) => {
  switch (actionName) {
    case UPDATE_GAME:
      return {
        type: UPDATE_GAME,
        data: {
          position: param[0],
          info: param[1],
        },
      };
    case USER_NAME:
      return {
        type: USER_NAME,
        data: {
          username: param[0],
        },
      };
    default:
      return {
        type: RESET_DATA,
      };
  }
};
